import React from 'react'
import { cloneDeep, mapValues } from 'lodash'
import { FlowChartWithState } from '@mrblenny/react-flow-chart'
import * as actions from '@mrblenny/react-flow-chart'

import Sidebar from './Sidebar'
import Content from './Holst'
import NodeInnerCustom from './NodeInnerCustom'
import { chartSimple, nodes} from '../constants'

class Tkit extends React.Component {

    constructor(props) {
        super(props)
        this.state = cloneDeep(chartSimple)
        this.isNodeEditmode = false
        this.activeNode = false
        this.stories = {
            nodes: {},
            links: this.state.links
        }

        // this.handleClickEvent = (value) => {
        //     console.log('done');
        //     console.log(value);
        //     //this.setState({ name: value })
        // }
    }

    handleNodeClick = (node) => {
        console.log(node.title);
    }

    render() {
        const chart = this.state
        const stateActions = mapValues(actions, (any) =>
            (...args) => this.setState(...args))
        return (

            <div className="Page">

                <Content>
                    <FlowChartWithState initialValue={chart} config={{
                        snapToGrid: true,
                    }} Components={{
                        NodeInner: NodeInnerCustom, //Here also PORT

                    }} callbacks={stateActions} />
                </Content>

                <Sidebar>
                    { chart.selected.type ? 
                        <div>
                            <div>Type: {chart.selected.type}</div>
                            <div>ID: {chart.selected.id}</div>
                            <br/>
                            {/*
                                We can re-use the onDeleteKey action. This will delete whatever is selected.
                                Otherwise, we have access to the state here so we can do whatever we want.
                            */}
                        </div>
                        : <div>Click on a Node, Port or Link</div> }
                 </Sidebar>

                <Sidebar data={nodes}/>


            </div>
        )
    }
}

export default Tkit
