const Node = ({ node, handleClickEvent }) => {

    return (
        <div className="node">
            <div onClick={handleClickEvent}>
                {node.properties.title}
            </div>
        </div>
    )
}

class NodeInnerCustom extends React.Component {

    constructor({ node, config, handleNodeClick}) {
        super();
        this.node = node;
        this.config = config;
        this.state = { value: '' }; //пусть инпут изначально пустой
        this.handleNodeClick = handleNodeClick
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
        console.log(this.state.value);
        console.log(this.node);
    }

    render() {

        switch (this.node.type) {
            case 'command':
                return (
                    <Node node={this.node} handleNodeClick={handleNodeClick} />
                )
            // case 'state':
            //     return (
            //         <Node node={this.node} handleClickEvent={this.handleClickEvent} />
