//                 <path fill="white" d="M7.41,8.58L12,13.17L16.59,8.58L18,10L12,16L6,10L7.41,8.58Z" />
//             </svg>
//         )}
//     </PortDefaultOuter>
// )

const Node = ({ node }) => {
    function cl () {
        console.log('node', node)
    }
    return (
        <div className="node">
            <div onClick={() => cl()}>
                {node.properties.title}
            </div>
        </div>
    )
}

class NodeInnerCustom extends React.Component {

    constructor({ node, config }) {
        super();
        this.node = node;
        this.config = config;
        this.state = { value: '' }; //пусть инпут изначально пустой
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
        console.log(this.state.value);
        console.log(this.node);
    }

    render() {
        // const { properties } = this.node;

        switch (this.node.type) {
            case 'command':
                return (
                    <Node node={this.node} />
                )
            case 'state':
                return (
                    <Node node={this.node} />
                )
            case 'action':
                return (
                    <Node node={this.node} />
                )
            case 'condition':
                return (
                    <Node node={this.node} />
                )

            default:
                return (
                    <div></div>
                )

        }
    }
}

export default NodeInnerCustom
