import React from 'react'
import { cloneDeep, mapValues } from 'lodash'
import { FlowChartWithState } from '@mrblenny/react-flow-chart'
import * as actions from '@mrblenny/react-flow-chart'

import Sidebar from './Sidebar'
import Content from './Holst'
import SidebarItem from './SidebarItem'
import NodeInnerCustom from './NodeInnerCustom'
import { chartSimple, commands } from '../constants'

class Tkit extends React.Component {

    constructor(props) {
        super(props)
        this.state = cloneDeep(chartSimple)
        this.stories = {
            links: this.links,
            nodes: {}
        }
    }

    log_stories = () => {
        console.log(this.stories);
        // var test = JSON.stringify(this.state);
        // console.log(test);
    }

    render() {
        const chart = this.state
        const stateActions = mapValues(actions, (any) =>
            (...args) => this.setState(...args))
        return (
            <div className="Page">''
                <Content>
                    <FlowChartWithState initialValue={chart} config={{
                        snapToGrid: true,
                    }} Components={{
                        NodeInner: NodeInnerCustom,

                    }} callbacks={stateActions} />
                </Content>

                <Sidebar>

                    <button onClick={this.log_stories}>Log stories</button>

                    {commands.nodes.map((command, index) => (
                        <SidebarItem
                            key={index}
                            type={command.type}
                            ports={command.ports}
                            properties={command.properties}
                        />
                    ))}

                </Sidebar>

            </div>
        )
    }
}

export default Tkit
