import React from 'react'
import { cloneDeep, mapValues } from 'lodash'
import { FlowChartWithState } from '@mrblenny/react-flow-chart'
import * as actions from '@mrblenny/react-flow-chart'

import Sidebar from './Sidebar'
import Content from './Holst'
import NodeInnerCustom from './NodeInnerCustom'
import { chartSimple, nodes} from '../constants'

import {
  IChart,
  IConfig,
  identity,
  IOnCanvasClick,
  IOnCanvasDrop,
  IOnDeleteKey,
  IOnDragCanvas,
  IOnDragCanvasStop,
  IOnDragNode,
  IOnDragNodeStop,
  IOnLinkCancel,
  IOnLinkClick,
  IOnLinkComplete,
  IOnLinkMouseEnter,
  IOnLinkMouseLeave,
  IOnLinkMove,
  IOnLinkStart,
  IOnNodeClick,
  IOnNodeDoubleClick,
  IOnNodeMouseEnter,
  IOnNodeMouseLeave,
  IOnNodeSizeChange,
  IOnPortPositionChange,
  IOnZoomCanvas,
  IStateCallback,
} from '../'

class Tkit extends React.Component {

    constructor() {
        super()
        this.state = cloneDeep(chartSimple)
        this.stories = {
            nodes: {},
            links: this.state.links
        }
    }

    // someMethod() {
    //     // Force a render with a simulated state change
    //     this.setState({ state: this.state });
    // }

    render() {
        const chart = this.state

        const stateActions = mapValues(actions, (any) =>
            (...args) => this.setState(...args))

        return(

            <div className="Page">

                <Content>
                    <FlowChartWithState initialValue={chart} config={{
                        snapToGrid: true,
                    }} Components={{
                        NodeInner: NodeInnerCustom, //ADD PORT CUSTOM

                    }} callbacks={stateActions} />
                </Content>

                { chart.selected.type ? 
                    <Sidebar isNodeEditMode={true} data={nodes} node={chart.selected}/>
                :
                    <Sidebar isNodeEditMode={false} data={nodes}/>
                }
            </div>
        )
    }
}


const App = () => {

    return (
        <Tkit/>
    )
}

export default App
